import './App.css';
import { v4 as uuidv4 } from 'uuid';
import { useState } from "react";

function App() {
  const sqrCnt = 64;
  let pieces = {};
  const [active, setActive] = useState(null);

  const createPiece = (x, y, isBlack=false) => {
    const id = uuidv4();
    pieces[id] = {
      id,
      x,
      y,
      isBlack: isBlack,
      isKing: false,
    }

    return id;
  };

  const checkSpace = (x, y) => {
    return Object.values(pieces).find((p) => p.x === x && p.y === y);
  }

  const getValidMoves = (id) => {
    const p = pieces[id];
    let validMoves = new Set();
    let toCheck = p.isKing ? [{dx: 1, dy: 1}, {dx: -1, dy: -1}, {dx: 1, dy: -1}, {dx: -1, dy: -1}] : (p.isBlack ? [{dx: 1, dy: 1}, {dx: -1, dy: 1}] : [{dx: 1, dy: -1}, {dx: -1, dy: -1}]);
    toCheck.forEach((x) => {
      if (p.x + x.dx <= 7 && p.y + x.dy <= 7 && p.x + x.dx >= 0 && p.y + x.dy  >= 0) {
        const check = checkSpace(p.x + x.dx, p.y + x.dy);
        if (check != null) {
          if (check.isBlack !== p.isBlack) {
            if (p.x + (2 * x.dx) <= 7 && p.y + (2 * x.dy) <= 7 && p.x + (2 * x.dx) >= 0 && p.y + (2 * x.dy) >= 0) {
              if (checkSpace(p.x + (2 * x.dx), p.y + (2 * x.dy)) === null) {
                validMoves.add(`${p.x + (2 * x.dx)}, ${p.y + (2 * x.dy)}`);
              }
            }
          }
        } else {
          validMoves.add(`${p.x + x.dx}, ${p.y + x.dy}`);
        }
      }
    });
    return validMoves
  };

  const activePiece = (id) => {
    setActive({
      id,
      availableMoves: getValidMoves(id)
    })
  };

  let sqrs = [];
  for (let i = 0; i < sqrCnt; i++) {
    const isBlack = (Math.floor(i/8) % 2 === 0 && (i % 8) % 2 === 0) || (Math.floor(i/8) % 2 === 1 && (i % 8) % 2 === 1);
    let obj = {
      x: i % 8,
      y: Math.floor(i / 8),
      coord: `(${Math.floor(i / 8)}, ${i % 8})`,
      space_color: isBlack ? 'black' : 'red',
    };
    if (obj.y < 3 && !isBlack) {
      obj.piece = createPiece(i % 8, Math.floor(i / 8), true);
    } else if (obj.y > 4 && isBlack) {
      obj.piece = createPiece(i % 8, Math.floor(i / 8), false);
    }
    sqrs.push(obj);
  }


  return (
    <div className="App">
      <header className="App-header">
        <h2>Checkers</h2>
        <div className="gameboard">
          {
            sqrs.map((x) => {
              return (
                <div className={"game-square " + x.space_color + (active?.availableMoves.has(x.coord) ? ' move' : '')} key={x.coord}>
                  {
                    x.piece ? <div onMouseDown={(e) => {activePiece(x.piece)}} className={"game-piece" + (pieces[x.piece].isBlack ? ' black' : '')}></div> : ""
                  }
                </div>
              );
            })
          }
        </div>
      </header>
    </div>
  );
}

export default App;
