import './App.css';
import { v4 as uuidv4 } from 'uuid';

function App() {
  const sqrCnt = 64;
  let pieces = {};

  const createPiece = (x, y, isBlack=false) => {
    const id = uuidv4();
    pieces[id] = {
      id,
      x,
      y,
      isBlack: isBlack,
      isKing: false,
    }

    return id;
  };

  let sqrs = [];
  for (let i = 0; i < sqrCnt; i++) {
    const isBlack = (Math.floor(i/8) % 2 === 0 && (i % 8) % 2 === 0) || (Math.floor(i/8) % 2 === 1 && (i % 8) % 2 === 1);
    let obj = {
      x: i % 8,
      y: Math.floor(i / 8),
      coord: `(${Math.floor(i / 8)}, ${i % 8})`,
      space_color: isBlack ? 'black' : 'red',
    };
    if (obj.y < 3 && isBlack) {
      obj.piece = createPiece(i % 8, Math.floor(i / 8), false);
    } else if (obj.y > 4 && !isBlack) {
      obj.piece = createPiece(i % 8, Math.floor(i / 8), true);
    }
    sqrs.push(obj);
  }


  return (
    <div className="App">
      <header className="App-header">
        <h2>Checkers</h2>
        <div className="gameboard">
          {
            sqrs.map((x) => {
              return (
                <div className={"game-square " + x.space_color} key={x.coord}>
                  {
                    x.piece ? <div className={"game-piece" + (pieces[x.piece].isBlack ? ' black' : '')}></div> : ""
                  }
                </div>
              );
            })
          }
        </div>
      </header>
    </div>
  );
}

export default App;
