import './App.css';
import { useState } from "react";
import { Checkers } from './Game';

const game = new Checkers()

function App() {
  const [active, setActive] = useState(null);

  const activePiece = (id) => {
    setActive({
      id,
      availableMoves: game.getValidMoves(id)
    })
  };


  return (
    <div className="App">
      <header className="App-header">
        <h2>Checkers</h2>
        <div className="gameboard">
          {
            game.board.map((x) => {
              return (
                <div onMouseDown={(e) => {if (active?.availableMoves.has(x.coord)) game.movePiece(x.id, active.id)}} className={"game-square " + x.space_color + (active?.availableMoves.has(x.coord) ? ' move' : '')} key={x.coord}>
                  <span className='coord'>{x.coord}</span>
                  {
                    x.piece && x.piece !== null ? <div onMouseDown={(e) => {activePiece(x.piece)}} className={"game-piece" + (game.pieces[x.piece].isBlack ? ' black' : '') + (active?.id === x.piece ? ' active' : '')}></div> : ""
                  }
                </div>
              );
            })
          }
        </div>
      </header>
    </div>
  );
}

export default App;
